--5.Print Number of Emplyoees by CountryRegionNameuse AdventureWorks2017Selectpcr.[Name]  Countryregionname, count(hre.BusinessEntityID) numberfrom[Person].[CountryRegion] pcr join[Person].[StateProvince] psp on pcr.CountryRegionCode=psp.CountryRegionCode join[Person].[Address] pa on psp.[StateProvinceID]=pa.StateProvinceID  join[Person].[BusinessEntityAddress] pbea on pbea.AddressID=pa.AddressID  join[HumanResources].[Employee] hre on hre.BusinessEntityID=pbea.BusinessEntityIDgroup bypcr.[Name]